def find_a_way(text):
    output = 0
    occurrence = 0
    map=[]
    for i in text:
        map.append(i)
    print (map)
    while True:
        for i in map:
            if i == "(":
                output += 1
                occurrence += 1
            elif i == ")":
                output -= 1
                occurrence += 1
            else:
                print("there is a bug in input")
            if output == -1:
                return output, occurrence

